        ],
        "properties": {
          "name": {
            "type": "string",
            "description": "角色姓名"
          },
          "role": {
            "type": "string",
            "enum": ["protagonist", "deuteragonist"],
            "description": "角色类型：protagonist=第一主角，deuteragonist=第二主角"
          },
          "identity": {
            "type": "string",
            "description": "身份职业/社会角色"
          },
          "traits": {
            "type": "array",
            "description": "核心性格特质关键词（3-5个）",
            "items": {
              "type": "string"
            },
            "minItems": 3,
            "maxItems": 5
          },
          "overt_motivation": {
            "type": "string",
            "description": "显性动机（表面目标）"
