        "emotion_verbs": {
          "type": "array",
          "description": "情感动词/核心动作（决定X轴剧情模式）",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "scene_elements": {
          "type": "array",
          "description": "场景要素/世界观元素（决定Y轴标签）",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "relationship_tension": {
          "type": "array",
          "description": "人物关系张力/冲突来源",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "user_preferences": {
          "type": "object",
          "description": "用户的情感偏好和禁忌",
          "properties": {
            "desired_emotion": {
              "type": "string",
              "description": "期望的情感倾向"
            },
            "forbidden": {
              "type": "array",
              "description": "明确拒绝的元素",
              "items": {
                "type": "string"
              }
            }
          }
        }
      }
    },
    "selected_recipe": {
      "type": "object",
      "description": "用户选定的创作配方（X-Y轴化学反应组合）",
      "required": [
