        "y_axis_tags",
        "reaction_strength",
        "tension_stars",
        "forbidden_elements"
      ],
      "properties": {
        "x_axis_mode_id": {
          "type": "string",
          "pattern": "^[A-G]\\d{1,2}$",
          "enum": [
            "A1", "A2", "A3", "A4", "A5", "A6",
            "B7", "B8", "B9", "B10", "B11", "B12",
            "C13", "C14", "C15", "C16", "C17", "C18",
            "D19", "D20", "D21", "D22", "D23",
            "E24", "E25", "E26", "E27", "E28",
            "F29", "F30", "F31", "F32", "F33", "F34",
            "G35", "G36"
          ],
          "description": "普罗蒂36种剧情模式的标识符"
        },
        "x_axis_mode_name": {
          "type": "string",
          "description": "X轴模式的中文名称"
        },
        "y_axis_tags": {
          "type": "array",
          "description": "现代网文核心标签（表达载体）",
          "items": {
            "type": "string",
            "enum": [
              "双向暗恋",
              "系统流",
              "无限流",
              "种田流",
              "赛博朋克",
              "规则怪谈",
              "重生/穿越",
              "商战权谋",
              "克苏鲁",
              "第四天灾",
              "仙侠修真",
              "刑侦悬疑"
            ]
          },
          "minItems": 2,
          "maxItems": 3
        },
        "reaction_strength": {
          "type": "string",
          "enum": ["↗", "↘", "✷"],
          "description": "X轴与Y轴的化学反应强度符号"
        },
        "tension_stars": {
          "type": "integer",
          "minimum": 1,
          "maximum": 5,
          "description": "故事张力指数（1-5星）"
        },
        "forbidden_elements": {
          "type": "array",
          "description": "避雷红线（创作禁忌元素）",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "x_axis_display": {
          "type": "string",
          "description": "X轴模式在故事中的具体显影方式"
        },
        "y_axis_combination_effect": {
          "type": "string",
          "description": "Y轴标签组合产生的效应描述"
        },
        "reaction_formula": {
          "type": "string",
          "description": "化学反应公式的一句话描述"
        },
        "platform_fit": {
          "type": "array",
          "description": "适合发布的平台类型",
          "items": {
            "type": "string"
          }
        }
      }
    },
    "one_sentence_synopsis": {
      "type": "string",
      "description": "一句话故事梗概（30-50字）",
      "minLength": 30,
      "maxLength": 50
    }
  }
}

