{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "$id": "characters.schema.json",
  "title": "小说人物设计",
  "description": "人物一致性的参考依据，避免角色崩坏",
  "type": "object",
  "required": ["main_characters"],
  "properties": {
    "main_characters": {
      "type": "array",
      "description": "主角/核心角色数组",
      "minItems": 1,
      "items": {
        "type": "object",
        "required": [
          "name",
          "role",
          "traits",
          "overt_motivation",
          "covert_motivation",
          "secret_fear"
        ],
        "properties": {
          "name": {
            "type": "string",
            "description": "角色姓名"
          },
          "role": {
            "type": "string",
            "enum": ["protagonist", "deuteragonist"],
            "description": "角色类型：protagonist=第一主角，deuteragonist=第二主角"
          },
          "identity": {
            "type": "string",
            "description": "身份职业/社会角色"
          },
          "traits": {
            "type": "array",
            "description": "核心性格特质关键词（3-5个）",
            "items": {
              "type": "string"
            },
            "minItems": 3,
            "maxItems": 5
          },
          "overt_motivation": {
            "type": "string",
            "description": "显性动机（表面目标）"
          },
          "covert_motivation": {
            "type": "string",
            "description": "隐性动机（深层需求）"
          },
          "secret_fear": {
            "type": "string",
            "description": "秘密恐惧（内心脆弱点）"
          },
          "misperception_of_other": {
            "type": "string",
            "description": "对其他主角的错位认知"
          },
          "hidden_connection": {
            "type": "string",
            "description": "与其他角色的隐藏连接"
          }
        }
      }
    },
    "supporting_characters": {
      "type": "array",
      "description": "配角/次要角色数组",
      "items": {
        "type": "object",
        "required": ["name", "function"],
        "properties": {
          "name": {
            "type": "string",
            "description": "配角姓名"
          },
          "function": {
            "type": "string",
            "enum": ["助力", "阻碍", "反转点"],
            "description": "对主角的作用类型"
          },
          "identity": {
            "type": "string",
            "description": "配角身份"
          },
          "description": {
            "type": "string",
            "description": "配角简要描述"
          }
        }
      }
    },
    "relationship_dynamics": {
      "type": "string",
      "description": "角色关系动态描述"
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "$id": "creative_intent.schema.json",
  "title": "小说创作意图配方",
  "description": "记录用户需求理解和配方选择结果，作为创作的北极星",
  "type": "object",
  "required": ["user_need", "inspiration_state", "extracted_elements", "selected_recipe", "one_sentence_synopsis"],
  "properties": {
    "user_need": {
      "type": "string",
      "description": "用户的原始输入文本",
      "minLength": 1
    },
    "inspiration_state": {
      "type": "string",
      "enum": ["no_inspiration", "vague_inspiration", "clear_inspiration"],
      "description": "用户创作时的灵感清晰程度"
    },
    "extracted_elements": {
      "type": "object",
      "description": "从用户输入中提取的创作关键要素",
      "required": ["emotion_verbs", "scene_elements", "relationship_tension"],
      "properties": {
        "emotion_verbs": {
          "type": "array",
          "description": "情感动词/核心动作（决定X轴剧情模式）",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "scene_elements": {
          "type": "array",
          "description": "场景要素/世界观元素（决定Y轴标签）",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "relationship_tension": {
          "type": "array",
          "description": "人物关系张力/冲突来源",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "user_preferences": {
          "type": "object",
          "description": "用户的情感偏好和禁忌",
          "properties": {
            "desired_emotion": {
              "type": "string",
              "description": "期望的情感倾向"
            },
            "forbidden": {
              "type": "array",
              "description": "明确拒绝的元素",
              "items": {
                "type": "string"
              }
            }
          }
        }
      }
    },
    "selected_recipe": {
      "type": "object",
      "description": "用户选定的创作配方（X-Y轴化学反应组合）",
      "required": [
        "x_axis_mode_id",
        "x_axis_mode_name",
        "y_axis_tags",
        "reaction_strength",
        "tension_stars",
        "forbidden_elements"
      ],
      "properties": {
        "x_axis_mode_id": {
          "type": "string",
          "pattern": "^[A-G]\\d{1,2}$",
          "enum": [
            "A1", "A2", "A3", "A4", "A5", "A6",
            "B7", "B8", "B9", "B10", "B11", "B12",
            "C13", "C14", "C15", "C16", "C17", "C18",
            "D19", "D20", "D21", "D22", "D23",
            "E24", "E25", "E26", "E27", "E28",
            "F29", "F30", "F31", "F32", "F33", "F34",
            "G35", "G36"
          ],
          "description": "普罗蒂36种剧情模式的标识符"
        },
        "x_axis_mode_name": {
          "type": "string",
          "description": "X轴模式的中文名称"
        },
        "y_axis_tags": {
          "type": "array",
          "description": "现代网文核心标签（表达载体）",
          "items": {
            "type": "string",
            "enum": [
              "双向暗恋",
              "系统流",
              "无限流",
              "种田流",
              "赛博朋克",
              "规则怪谈",
              "重生/穿越",
              "商战权谋",
              "克苏鲁",
              "第四天灾",
              "仙侠修真",
              "刑侦悬疑"
            ]
          },
          "minItems": 2,
          "maxItems": 3
        },
        "reaction_strength": {
          "type": "string",
          "enum": ["↗", "↘", "✷"],
          "description": "X轴与Y轴的化学反应强度符号"
        },
        "tension_stars": {
          "type": "integer",
          "minimum": 1,
          "maximum": 5,
          "description": "故事张力指数（1-5星）"
        },
        "forbidden_elements": {
          "type": "array",
          "description": "避雷红线（创作禁忌元素）",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "x_axis_display": {
          "type": "string",
          "description": "X轴模式在故事中的具体显影方式"
        },
        "y_axis_combination_effect": {
          "type": "string",
          "description": "Y轴标签组合产生的效应描述"
        },
        "reaction_formula": {
          "type": "string",
          "description": "化学反应公式的一句话描述"
        },
        "platform_fit": {
          "type": "array",
          "description": "适合发布的平台类型",
          "items": {
            "type": "string"
          }
        }
      }
    },
    "one_sentence_synopsis": {
      "type": "string",
      "description": "一句话故事梗概（30-50字）",
      "minLength": 30,
      "maxLength": 50
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "$id": "outline.schema.json",
  "title": "小说故事大纲（三层结构）",
  "description": "节奏规划参考，确保故事有合理的起承转合。三层设计：act故事梗概 → chapter故事梗概 → scene场景序列",
  "type": "object",
  "required": ["act_one", "act_two", "act_three"],
  "properties": {
    "act_one": {
      "type": "object",
      "description": "第一幕：开篇（20-30%篇幅）- 建立世界观、引入主角和核心冲突",
      "required": ["description", "story_synopsis", "key_chapters", "turning_point"],
      "properties": {
        "description": {
          "type": "string",
          "description": "该幕的核心任务描述"
        },
        "story_synopsis": {
          "type": "string",
          "description": "本幕故事梗概（自然语言叙述，500-1000字）- 完整描述本幕的故事发展、主角行动、冲突转折、配角影响、状态变化",
          "minLength": 500,
          "maxLength": 1000
        },
        "key_chapters": {
          "type": "array",
          "description": "该幕的关键章节数组",
          "minItems": 1,
          "items": {
            "type": "object",
            "required": ["chapter_number", "chapter_title", "chapter_synopsis", "scene_sequence"],
            "properties": {
              "chapter_number": {
                "type": "integer",
                "minimum": 1,
                "description": "章节编号"
              },
              "chapter_title": {
                "type": "string",
                "description": "章节标题"
              },
              "chapter_synopsis": {
                "type": "string",
                "description": "本章故事梗概（自然语言叙述，200-400字）- 描述起始状态、主角核心行动、主要冲突展开、在整幕中的作用、结束状态",
                "minLength": 200,
                "maxLength": 400
              },
              "scene_sequence": {
                "type": "array",
                "description": "本章场景序列（按时间顺序排列，至少3个场景）",
                "minItems": 3,
                "items": {
                  "type": "object",
                  "required": [
                    "scene_number",
                    "scene_location",
                    "characters_present",
                    "scene_purpose",
                    "core_action",
                    "conflict_point",
                    "emotional_shift",
                    "scene_outcome"
                  ],
                  "properties": {
                    "scene_number": {
                      "type": "integer",
                      "minimum": 1,
                      "description": "场景编号（章内序号）"
                    },
                    "scene_location": {
                      "type": "string",
                      "description": "场景地点（具体环境）"
                    },
                    "characters_present": {
                      "type": "array",
                      "description": "在场角色",
                      "items": {
                        "type": "string"
                      },
                      "minItems": 1
                    },
                    "scene_purpose": {
                      "type": "string",
                      "description": "本场景在chapter_synopsis中的作用"
                    },
                    "core_action": {
                      "type": "string",
                      "description": "核心动作（谁对谁做了什么，必须是可视化的具体行为，不能是抽象表述）"
                    },
                    "conflict_point": {
                      "type": "string",
                      "description": "本场景的冲突爆发点"
                    },
                    "emotional_shift": {
                      "type": "string",
                      "description": "本场景中主角的情感变化（从A到B）"
                    },
                    "scene_outcome": {
                      "type": "string",
                      "description": "本场景的结果（为下一场景埋伏笔）"
                    }
                  }
                }
              },
              "emotional_tone": {
                "type": "string",
                "description": "本章整体情感基调"
              }
            }
          }
        },
        "turning_point": {
          "type": "string",
